    private void jTableLivreMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTableLivreMouseClicked
        try {
            int row = jTableLivre.getSelectedRow();
            String Table_click = (jTableLivre.getModel().getValueAt(row, 0).toString());
            String req = "SELECT idEditeur, nomEditeur,"
            + " noRueEditeur, rueEditeur,"
            + " codePostalEditeur, villeEditeur,"
            + " paysEditeur,  telephoneEditeur,"
            + " emailEditeur, supprimeEditeur"
            + " from Editeur WHERE IdEditeur='" + Table_click + "' ";
            ResultSet rs = livre.selectionner(req);
            if (rs.next()) {
                String add1 = rs.getString("isbnLivre");
                txtISBN.setText(add1);
                String add2 = rs.getString("nomEditeur");
                txtnom.setText(add2);
                String add3 = rs.getString("noRueEditeur");
                txtnrue.setText(add3);
                String add4 = rs.getString("rueEditeur");
                txtrue.setText(add4);
                String add5 = rs.getString("codePostalEditeur");
                txtcodePostal.setText(add5);
                String add6 = rs.getString("villeEditeur");
                txtville.setText(add6);
                String add7 = rs.getString("paysEditeur");
                txtpays.setText(add7);
                String add8 = rs.getString("telephoneEditeur");
                txttel.setText(add8);
                String add9 = rs.getString("emailEditeur");
                txtemail.setText(add9);
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);

        }
    }//GEN-LAST:event_jTableLivreMouseClicked

