    private void txtRechercheKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtRechercheKeyReleased
        // TODO add your handling code here:

        try{
            String req="SELECT idEditeur AS ID, nomEditeur AS NOM,"
            + " noRueEditeur AS NUM, rueEditeur AS RUE,"
            + " codePostalEditeur AS CP, villeEditeur AS VILLE,"
            + " paysEditeur AS PAYS,  telephoneEditeur AS TEL,"
            + " emailEditeur AS EMAIL, supprimeEditeur AS SUPPR"
            + " from Editeur"
            + " where nomEditeur+telephoneEditeur+emailEditeur+villeEditeur+codePostalEditeur like ?";
            ResultSet rs = livre.rechercher(req,"%"+txtRecherche.getText()+"%");
            jTableLivre.setModel(DbUtils.resultSetToTableModel(rs));
        }catch(Exception e){

            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_txtRechercheKeyReleased


